    private void invertirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_invertirActionPerformed
        if(invertir.isSelected()== false){
             convertir.setText("Convertir (euros a ptas)");
         } else {
             convertir.setText("Convertir (ptas a euros)");
         }
    }//GEN-LAST:event_invertirActionPerformed

    private void convertirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_convertirActionPerformed
       double cantidad = Double.parseDouble(input.getText());
       
        if(invertir.isSelected()== false){
             cantidad=cantidad*166.386;
             mostrarResultado.setText((float)cantidad+" pesetas");
         } else {
             cantidad=cantidad/166.386;
             mostrarResultado.setText((float)cantidad+" euros");
         }
        
       
    }//GEN-LAST:event_convertirActionPerformed

